select a.Fecha, concat(b.Nombres, " " , b.Apellidos) as Empleado, a.FormaDePago, concat(c.Nombres, " ",c.Apellidos) as Cliente, c.Direccion, d.Cantidad, d.NombreProducto , d.Precio, d.Exento, d.NoSujeto, d.Gravado, d.SubTotal, a.TotalAPagar  from facturasdeventas a, empleados b, clientes c, detallesfacturas d where a.IDEmpleado = b.IDEmpleado and a.IDCliente = c.IDCliente and a.IDFacturaDeVenta=d.IDFacturaDeVenta;
